import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import './App.css';
import reportWebVitals from './reportWebVitals';

class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'b',
    };
  }

  render() {
    return (
      <div
        className="boxy">
        {this.state.value}
      </div>
    );
  }
}

class Column extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div className="board-column">
          <Dropper col={this.props.col}/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>  
      </div>
    )
  }
}

class Grid extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div>
        <div className="board-column">
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>          
        </div>
        <div className="board-column">
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>          
        </div>
        <div className="board-column">
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>          
        </div>
      </div>
    )
  }
}

class Dropper extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      col: props.col
    }

  }
  render(){
    return(
      <button className="dropper">
        {this.state.col}
      </button>
    )
  }
}

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      placements: new Array(6).fill(new Array(7).fill(0))
    }
    let a = "a";
  }

  render(){
    return (
      <div className="game">
        <div className="button-row">
          <Column col={0}/>
          <Column col={1}/>
          <Column col={2}/>
          <Column col={3}/>
          <Column col={4}/>
          <Column col={5}/>
          <Column col={6}/>
        </div>
      </div>


    );
  }

}

ReactDOM.render(
    <App />,
  document.getElementById('root')
);

