import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import reportWebVitals from './reportWebVitals';

class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'a',
    };
  }

  render() {
    return (
      <button
        className="square"
        onClick={() => this.setState({value: 'X'})}
      >
        {this.state.value}
      </button>
    );
  }
}

class Grid extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div>
        <div className="board-row">
          <Square/>
          <Square/>
          <Square/>
        </div>
        <div className="board-row">
          <Square/>
          <Square/>
          <Square/>

        </div>
        <div className="board-row">
          <Square/>
          <Square/>
          <Square/>

        </div>
      </div>
    )
  }
}
class App extends React.Component() {
  constructor(props){
    super(props);
  }

  render(){
    return (
      <div className="game">
        <div className="game-board">
          <Grid/>
        </div>
      </div>


    );
  }

}

ReactDOM.render(
    <App />,
  document.getElementById('root')
);

