  constructor(props) {
    super(props);
    this.state = {
      value: 'b',
    };
  }

  render() {
    return (
      <div
        className="boxy">
        {this.state.value}
      </div>
    );
  }
}

class Grid extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div>
