          <Square/>

        </div>
        <div className="board-row">
          <Square/>
          <Square/>
          <Square/>

        </div>
      </div>
    )
  }
}
class App extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return (
      <div className="game">
        <div className="game-board">
          <Grid/>
        </div>
      </div>


    );
  }

}

ReactDOM.render(
