import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import './App.css';
import reportWebVitals from './reportWebVitals';

class Square extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 'b',
    };
  }

  render() {
    return (
      <div
        className="boxy">
        {this.state.value}
      </div>
    );
  }
}

class Column extends React.Component{
  constructor(props){
    this.state={
      values: props.values,
    };
    super(props);
  }

  render(){
    return(
      <div className="board-column">
          <Dropper col={this.props.col}/>
          <Square val={this.state.values[0]}/>
          <Square val={this.state.values[1]}/>
          <Square val={this.state.values[2]}/>
          <Square val={this.state.values[3]}/>
          <Square val={this.state.values[4]}/>
          <Square val={this.state.values[5]}/>  
      </div>
    )
  }
}

class Grid extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div>
        <div className="board-column">
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>          
        </div>
        <div className="board-column">
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>          
        </div>
        <div className="board-column">
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>
          <Square/>          
        </div>
      </div>
    )
  }
}

class Dropper extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      col: props.col
    }

  }
  render(){
    return(
      <button className="dropper">
        {this.state.col}
      </button>
    )
  }
}

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      placements: new Array(7).fill(new Array(6).fill(0))
    }
    let a = "a";
  }

  render(){
    return (
      <div className="game">
        <div className="button-row">
          <Column col={0} values={this.state.placements[0]}/>
          <Column col={1} values={this.state.placements[1]}/>
          <Column col={2} values={this.state.placements[2]}/>
          <Column col={3} values={this.state.placements[3]}/>
          <Column col={4} values={this.state.placements[4]}/>
          <Column col={5} values={this.state.placements[5]}/>
          <Column col={6} values={this.state.placements[6]}/>
        </div>
      </div>


    );
  }

}

ReactDOM.render(
    <App />,
  document.getElementById('root')
);

